const alerts_lz = document.querySelectorAll(".Alert");
if(alerts_lz) {
  alerts_lz.forEach(alert => {
    if (alert.getAttribute("tpClose")) {
      alert.querySelector(".img").onclick = () => {
        alert.classList.add(alert.getAttribute("tpClose"))
        setTimeout(() => {
          alert.remove()
        }, 500)
      }
  
    }
  });
}